package programs;

public class TestFor {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int a=5;
		for (int i = a; i >= 1; i--) 
        {
            for(int k=i;k<=a;k++)
			{
				System.out.print(k);
			}
            System.out.println();
        }
         
        
	}

}
